jQuery(document).ready(function(){HAPPYRIDER_GLOBALS.media_frame=null;HAPPYRIDER_GLOBALS.media_link=""});function happyrider_show_media_manager(a){HAPPYRIDER_GLOBALS.media_link=jQuery(a);if(HAPPYRIDER_GLOBALS.media_frame){HAPPYRIDER_GLOBALS.media_frame.open();return false}HAPPYRIDER_GLOBALS.media_frame=wp.media({title:HAPPYRIDER_GLOBALS.media_link.data("choose"),library:{type:"image"},multiple:HAPPYRIDER_GLOBALS.media_link.data("multiple")===true?"add":false,button:{text:HAPPYRIDER_GLOBALS.media_link.data("update"),close:true}});HAPPYRIDER_GLOBALS.media_frame.on("select",function(b){var c=jQuery("#"+HAPPYRIDER_GLOBALS.media_link.data("linked-field")).eq(0);var e="";if(HAPPYRIDER_GLOBALS.media_link.data("multiple")===true){HAPPYRIDER_GLOBALS.media_frame.state().get("selection").map(function(f){e+=(e?"\n":"")+f.toJSON().url});var d=c.val();e=d+(d?"\n":"")+e}else{e=HAPPYRIDER_GLOBALS.media_frame.state().get("selection").first().toJSON().url}c.val(e);c.trigger("change")});HAPPYRIDER_GLOBALS.media_frame.open();return false};